import { queryAllUser, getAllMiniRoleList, editMiniUser, getAllGame, editMiniUserGrant } from '@/api/index';
export default {
  name: "miniUser",
  data: function data() {
    var _this = this;

    return {
      columns: [{
        type: 'selection',
        width: 60,
        align: 'center',
        fixed: 'left'
      }, {
        type: 'index',
        width: 60,
        align: 'center',
        fixed: 'left'
      }, {
        title: 'openID',
        key: 'openId',
        width: 150
      }, {
        title: '微信名',
        key: 'name',
        width: 150
      }, // {
      //     title: '微信图片地址',
      //     key: 'avatarUrl',
      //     width: 300
      // },
      {
        title: "头像",
        key: "avatarUrl",
        width: 80,
        align: "center",
        render: function render(h, params) {
          return h("Avatar", {
            props: {
              src: params.row.avatarUrl
            }
          });
        }
      }, {
        title: '操作',
        key: 'action',
        width: 200,
        align: 'center',
        render: function render(h, params) {
          var enableOrDisable = '';
          var editButton = h('Button', {
            props: {
              type: 'primary',
              size: 'small',
              disabled: !_this.$route.meta.permTypes.includes('edit')
            },
            style: {
              marginRight: '5px'
            },
            on: {
              click: function click() {
                _this.edit(params.row);
              }
            }
          }, '角色分配');
          var grantButton = h('Button', {
            props: {
              type: 'primary',
              size: 'small',
              disabled: !_this.$route.meta.permTypes.includes('add')
            },
            style: {
              marginRight: '5px'
            },
            on: {
              click: function click() {
                _this.add(params.row);
              }
            }
          }, '授权游戏');
          return h('div', [editButton, grantButton]);
        }
      }],
      data: [],
      searchForm: {
        name: '',
        pageNumber: 1,
        pageSize: 10
      },
      miniUserForm: [],
      userForm: {
        roles: []
      },
      gameForm: {
        games: []
      },
      roleList: [],
      modalType: 1,
      modalTitle: '分配角色',
      modalGrantTitle: '分配游戏',
      ModalVisible: false,
      ModalVisibleGrant: false,
      total: 0,
      submitLoading: false,
      games: []
    };
  },
  mounted: function mounted() {
    this.init();
    this.getRoleList();
    this.getGames();
  },
  methods: {
    init: function init() {
      this.queryAll();
    },
    handleSearch: function handleSearch() {
      this.queryAll();
    },
    queryAll: function queryAll() {
      var _this2 = this;

      queryAllUser(this.searchForm).then(function (res) {
        if (res.success) {
          _this2.data = res.msg.records;
          _this2.total = res.count;
        }
      });
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.queryAll();
      this.clearSelectAll();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.queryAll();
    },
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    edit: function edit(v) {
      this.modalType = 1;
      this.modalTitle = '角色分配';
      this.$refs.userForm.resetFields();
      var openId = v.openId; // 转换null为""

      for (var attr in v) {
        if (v[attr] == null) {
          v[attr] = '';
        }
      }

      var str = JSON.stringify(v);
      var data = JSON.parse(str);
      this.miniUserForm = data;
      this.ModalVisible = true;
    },
    add: function add(v) {
      this.modalGrantTitle = '分配游戏';
      this.$refs.userForm.resetFields();
      var openId = v.openId; // 转换null为""

      for (var attr in v) {
        if (v[attr] == null) {
          v[attr] = '';
        }
      }

      var str = JSON.stringify(v);
      var data = JSON.parse(str);
      this.miniUserForm = data;
      this.ModalVisibleGrant = true;
    },
    submitMiniUser: function submitMiniUser() {
      var _this3 = this;

      this.$refs.userForm.validate(function (valid) {
        if (valid) {
          // 编辑
          _this3.submitLoading = true;
          editMiniUser({
            openId: _this3.miniUserForm.openId,
            roleId: _this3.userForm.roles
          }).then(function (res) {
            _this3.submitLoading = false;

            if (res.success) {
              _this3.$Message.success("操作成功");

              _this3.ModalVisible = false;
            }
          });
        }
      });
    },
    submitMiniGrant: function submitMiniGrant() {
      var _this4 = this;

      this.$refs.gameForm.validate(function (valid) {
        if (valid) {
          // 编辑
          _this4.submitLoading = true;
          editMiniUserGrant({
            openId: _this4.miniUserForm.openId,
            gameIds: _this4.gameForm.games
          }).then(function (res) {
            _this4.submitLoading = false;

            if (res.success) {
              _this4.$Message.success("操作成功");

              _this4.ModalVisibleGrant = false;
            }
          });
        }
      });
    },
    getRoleList: function getRoleList() {
      var _this5 = this;

      getAllMiniRoleList().then(function (res) {
        if (res.success) {
          _this5.roleList = JSON.parse(res.msg);
        }
      });
    },
    getGames: function getGames() {
      var _this6 = this;

      getAllGame().then(function (res) {
        if (res.success) {
          _this6.games = JSON.parse(res.msg);
        }
      });
    }
  }
};
