import {queryAllUser, getAllMiniRoleList, editMiniUser, getAllGame, editMiniUserGrant} from '@/api/index';

export default {
    name: "miniUser",
    data() {
        return {
            columns: [
                {
                    type: 'selection',
                    width: 60,
                    align: 'center',
                    fixed: 'left'
                },
                {
                    type: 'index',
                    width: 60,
                    align: 'center',
                    fixed: 'left'
                },
                {
                    title: 'openID',
                    key: 'openId',
                    width: 150
                },
                {
                    title: '微信名',
                    key: 'name',
                    width: 150
                },
                // {
                //     title: '微信图片地址',
                //     key: 'avatarUrl',
                //     width: 300
                // },
                {
                    title: "头像",
                    key: "avatarUrl",
                    width: 80,
                    align: "center",
                    render: (h, params) => {
                        return h("Avatar", {
                            props: {
                                src: params.row.avatarUrl
                            }
                        });
                    }
                },
                {
                    title: '操作',
                    key: 'action',
                    width: 200,
                    align: 'center',
                    render: (h, params) => {
                        let enableOrDisable = '';
                        let editButton = h(
                            'Button',
                            {
                                props: {
                                    type: 'primary',
                                    size: 'small',
                                    disabled: !this.$route.meta.permTypes.includes('edit')
                                },
                                style: {marginRight: '5px'},
                                on: {
                                    click: () => {
                                        this.edit(params.row);
                                    }
                                }
                            },
                            '角色分配'
                        );
                        let grantButton = h(
                            'Button',
                            {
                                props: {
                                    type: 'primary',
                                    size: 'small',
                                    disabled: !this.$route.meta.permTypes.includes('add')
                                },
                                style: {marginRight: '5px'},
                                on: {
                                    click: () => {
                                        this.add(params.row);
                                    }
                                }
                            },
                            '授权游戏'
                        );
                        return h('div', [editButton, grantButton]);
                    }
                }
            ],
            data: [],
            searchForm: {
                name: '',
                pageNumber: 1,
                pageSize: 10
            },
            miniUserForm: [],
            userForm: {
                roles: []
            },
            gameForm: {
                games: []
            },
            roleList: [],
            modalType: 1,
            modalTitle: '分配角色',
            modalGrantTitle: '分配游戏',
            ModalVisible: false,
            ModalVisibleGrant: false,
            total: 0,
            submitLoading: false,
            games: [],
        }
            ;
    },
    mounted() {
        this.init();
        this.getRoleList();
        this.getGames();
    },
    methods: {
        init() {
            this.queryAll();
        },
        handleSearch() {
            this.queryAll();
        },
        queryAll() {
            queryAllUser(this.searchForm).then(res => {
                if (res.success) {
                    this.data = res.msg.records;
                    this.total = res.count;
                }
            });
        },
        changePage(v) {
            this.searchForm.pageNumber = v;
            this.queryAll();
            this.clearSelectAll();
        },
        changePageSize(v) {
            this.searchForm.pageSize = v;
            this.queryAll();
        },
        clearSelectAll() {
            this.$refs.table.selectAll(false);
        },
        edit(v) {
            this.modalType = 1;
            this.modalTitle = '角色分配';
            this.$refs.userForm.resetFields();
            let openId = v.openId;

            // 转换null为""
            for (let attr in v) {
                if (v[attr] == null) {
                    v[attr] = '';
                }
            }
            let str = JSON.stringify(v);
            let data = JSON.parse(str);
            this.miniUserForm = data;
            this.ModalVisible = true;
        },
        add(v) {
            this.modalGrantTitle = '分配游戏';
            this.$refs.userForm.resetFields();
            let openId = v.openId;

            // 转换null为""
            for (let attr in v) {
                if (v[attr] == null) {
                    v[attr] = '';
                }
            }
            let str = JSON.stringify(v);
            let data = JSON.parse(str);
            this.miniUserForm = data;
            this.ModalVisibleGrant = true;
        },
        submitMiniUser() {
            this.$refs.userForm.validate(valid => {
                if (valid) {
                    // 编辑
                    this.submitLoading = true;
                    editMiniUser({
                        openId: this.miniUserForm.openId,
                        roleId: this.userForm.roles,
                    }).then(res => {
                        this.submitLoading = false;
                        if (res.success) {
                            this.$Message.success("操作成功");
                            this.ModalVisible = false;
                        }
                    });
                }
            });
        },
        submitMiniGrant() {
            this.$refs.gameForm.validate(valid => {
                if (valid) {
                    // 编辑
                    this.submitLoading = true;
                    editMiniUserGrant({
                        openId: this.miniUserForm.openId,
                        gameIds: this.gameForm.games,
                    }).then(res => {
                        this.submitLoading = false;
                        if (res.success) {
                            this.$Message.success("操作成功");
                            this.ModalVisibleGrant = false;
                        }
                    });
                }
            });
        },
        getRoleList() {
            getAllMiniRoleList().then(res => {
                if (res.success) {
                    this.roleList = JSON.parse(res.msg);
                }
            });
        },
        getGames() {
            getAllGame().then(res => {
                if (res.success) {
                    this.games = JSON.parse(res.msg);
                }
            })
        },
    }
}
